import React, { useEffect, useState } from 'react';
import classNames from "classnames";
// import { useDebounce } from 'use-debounce';
import './App.css';

const GameState = {
    Loading: 'Loading',
    Ready: 'Ready',
    Started: 'Started'
}
const ShapeSide = {
    Left: 'left',
    Right: 'right',
}
export default function App() {
    const [name, setName] = useState<string>('');
    const [gameState, setGameState] = useState<string>();
    const [shapeSide, setShapeSide] = useState<string>();
    // const [isSuccess, setIsSuccess] = useState<boolean>();
    const [userAnswer, setUserAnswer] = useState<string>();
    const [id, setId] = useState<NodeJS.Timer>();

    useEffect(() => {
        console.log('useEffect')
        if (gameState === GameState.Loading && !id) {
            const id = setTimeout(() => {
                setGameState(GameState.Ready)
                setId(undefined)
            }, 3000)
            setId(id)
        } else if (gameState === GameState.Ready && !id) {
            setTimeout(() => {
                // Change Game state
                setGameState(GameState.Started)
                setId(undefined)

                const id = setInterval(() => {
                    // Change image side
                    const screenSide = Math.random() > 0.5 ? ShapeSide.Right : ShapeSide.Left;
                    console.log(screenSide)
                    // setIsSuccess(undefined)
                    setUserAnswer('')
                    setShapeSide(screenSide)
                }, 1000)
                setId(id)

            }, 2000)
        }
    }, [gameState, id])

    useEffect(() => {
        window.addEventListener('keypress', e => {
            if (!userAnswer) {
                // console.log(e.key)
                // let isSuccess: boolean = false;
                // if (e.key.toLowerCase() === 'a' && shapeSide === ShapeSide.Left) isSuccess = true
                // if (e.key.toLowerCase() === 'l' && shapeSide === ShapeSide.Right) isSuccess = true
                // setIsSuccess(isSuccess)
                setUserAnswer(e.key.toLowerCase())
            }
        });
    }, [/*isSuccess,shapeSide*/ userAnswer]);

    const onChangeName = (event: any) => {
        setName(event.target.value)
    }

    const onRestartClick = () => {
        console.log('onRestartClick')
        setGameState(GameState.Loading)
    }

    console.log(gameState)

    // const isSuccess = userAnswer && shapeSide ? (userAnswer===shapeSide.substring(0,1) ? 'hit' : 'fail') : ''}
    // console.log(shapeSide?.substring(0,1))
    let answerClass = '';
    if (userAnswer && shapeSide) {
        if (userAnswer=== 'a' && shapeSide === ShapeSide.Left) answerClass='hit'
        if (userAnswer=== 'l' && shapeSide === ShapeSide.Right) answerClass='hit'
        else answerClass='fail'
    }

    return (
        <div className="App">
            {/* Header */}
            <h1>Shape-Game</h1>

            {/* Details */}
            <div>
                <input type="text" placeholder='Your name' value={name} onChange={onChangeName} disabled={!!gameState} />
                <input type="button" value="Start Game!" onClick={onRestartClick} disabled={!!gameState} />
            </div>

            {/* Image Viewer */}
            {gameState === GameState.Loading &&
                <div>Loading...</div>
            }
            {gameState === GameState.Ready &&
                <div>Ready :)</div>
            }
            {gameState === GameState.Started && shapeSide &&
                <div className={`${shapeSide}-image ${answerClass}`}>
                    <img src="/gift.png" className="image" alt="" />
                </div>
            }

        </div>
    )
};
