import React, { useEffect, useState } from 'react';
import { useDebounce } from 'use-debounce';
import './App.css';

export default function App() {
    const [name, setName] = useState('');
    const [shapeSide, setShapeSide] = useState('');
    const [id, setId] = useState<NodeJS.Timer>();
    const [isGameStarted, setIsGameStarted] = useState<boolean>();
    // const [shapeSideDebounced] = useDebounce(shapeSide, 1000);

    useEffect(() => {
        console.log('useEffect')
        // if (!id) {
        //     randomizeShape(1000)
        // }
    }, [id])

    const randomizeShape = (milis: number) => {
        console.log('randomizeShape')
        const id = setInterval(() => {
            // const Marh
            const screenSide = Math.random() > 0.5 ? 'right' : 'left';
            setShapeSide(screenSide)
        }, milis)
        setId(id)
    }
    const onChangeName = (event: any) => {
        setName(event.target.value)
    }

    const onRestartClick = () => {
        console.log('onRestartClick')
        setIsGameStarted(true)
        randomizeShape(3000)
    }


    return (
        <div className="App">
            {/* Header */}
            <h1>Shape-Game</h1>

            {/* Details */}
            <div className={''}>
                <input type="text" value={name} onChange={onChangeName} disabled={isGameStarted} />
                <input type="button" value="Start!" onClick={onRestartClick} disabled={isGameStarted} />
            </div>

            {/* Image Viewer */}
            { isGameStarted && shapeSide &&
                <div className={`${shapeSide}-image`}>
                    <img src="/gift.png" className="image" alt="" />
                </div>
            }

        </div>
    )
};
