import React, { useEffect, useMemo, useState } from 'react';
import './App.css';
import { randomIntFromInterval } from './utils';

const GameState = {
    Loading: 'Loading',
    // Ready: 'Ready',
    Started: 'Started',
    GameOver: 'GameOver',
}
const ShapeSide = {
    Left: 'left',
    Right: 'right',
}
const LevelStateType = {
    Error1: {
        state: 'Mistake',
        message: 'Too Soon',
        messageInfo: 'User taps any key in the waiting state'
    },
    Error2: {
        state: 'Mistake',
        message: 'Wrong Key',
        messageInfo: 'User taps an incorrect key in the indicator showing state'
    },
    Error3: {
        state: 'Mistake',
        message: 'Too Late',
        messageInfo: 'The indicator disappears before the user types any key'
    },
    Success: {
        state: 'Success'
    }
}
export default function App() {
    const [name, setName] = useState<string>('');
    const [gameState, setGameState] = useState<string>('');
    const [shapeSide, setShapeSide] = useState<string>();
    const [levelInfo, setLevelInfo] = useState<any>({})
    const [level, setLevel] = useState<number>(0);
    const [id, setId] = useState<NodeJS.Timer>();
    const [userAnswer, setUserAnswer] = useState<string>();

    const timeoutCallback = () => {
        if (level) {
            if (levelInfo.state===LevelStateType.Error2.state) {
                // User taps an incorrect key in the indicator showing state
                // setGameState(GameState.GameOver)
                return
            }
            if (!userAnswer && level) {
                // The indicator disappears before the user types any key
                setLevelInfo(LevelStateType.Error3)
                return
            }
            setGameState(GameState.GameOver)
        }
        // Change image side 
        const screenSide = Math.random() > 0.5 ? ShapeSide.Right : ShapeSide.Left;
        setUserAnswer(undefined)
        setShapeSide(screenSide)
        setLevel(prev => prev + 1)
        setId(undefined)
    }

    useEffect(() => {
        if (gameState === GameState.Loading) {
            if (userAnswer) {
                setLevelInfo(LevelStateType.Error1)
                setGameState(GameState.GameOver)
            }
            if (!id) {
                const LoadingTime = 1000//randomIntFromInterval(2000, 5000)
                const id = setTimeout(() => {
                    setGameState(GameState.Started)
                    setId(undefined)
                }, LoadingTime)
                setId(id)
            }
        } else if (gameState === GameState.Started) {
            if (!id) {
                const id = setTimeout(timeoutCallback as any, 3000)
                setId(id)
            }
            if (userAnswer && shapeSide) {
                if ((userAnswer === 'a' && shapeSide === ShapeSide.Left)
                    || (userAnswer === 'l' && shapeSide === ShapeSide.Right)) {
                    setLevelInfo(LevelStateType.Success)
                } else {
                    setLevelInfo(LevelStateType.Error2)
                    // setGameState(GameState.GameOver)
                }
            }
        } else if (gameState === GameState.GameOver) {
            clearTimeout(id)
        }
    }, [gameState, id, userAnswer, shapeSide, level, timeoutCallback])

    const onChangeName = (event: any) => {
        setName(event.target.value)
    }

    const onRestartClick = () => {
        setGameState(GameState.Loading)
        setShapeSide('')
        setLevelInfo({})
        setLevel(0)
        setId(undefined)
        setUserAnswer('')
        initWindowListener()
    }

    const initWindowListener = () => {
        window.removeEventListener('keypress', () => {
            window.addEventListener('keypress', e => {
                if (!userAnswer) {
                    setUserAnswer(e.key.toLowerCase())
                }
            });
        })
    }

    let answerClass = ''
    if (levelInfo.state===LevelStateType.Success.state) answerClass='hit'
    if (levelInfo.state===LevelStateType.Error1.state) answerClass='fail'

    return (
        <div className="App">
            {/* Header */}
            <h1>Shape-Game</h1>

            {/* Details */}
            <div>
                <input type="text" placeholder='Your name' value={name}
                    onChange={onChangeName}
                    disabled={[GameState.Loading, GameState.Started].includes(gameState)} />
                <input type="button" value="Start Game!"
                    onClick={onRestartClick}
                    disabled={[GameState.Loading, GameState.Started].includes(gameState)} />
            </div>

            {/* Image Viewer */}
            {gameState === GameState.Loading &&
                <div>Loading...</div>
            }
            {gameState === GameState.Started && shapeSide &&
                <div>
                    <div>Level:<span>{level}</span></div>
                    <div className={`${shapeSide}-image ${answerClass}`}>
                        <img src="/gift.png" className="image" alt="" />
                    </div>
                </div>
            }
            {gameState === GameState.GameOver &&
                <div>
                    <h2>Game Over :(</h2>
                    <h3>{levelInfo.message}</h3>
                    <h4>Level:<span>{level}</span></h4>
                    <img src="/game-over1.png" className="game-over" alt="" />
                </div>
            }

        </div>
    )
};
