import React, { useEffect, useState } from 'react';
// import { useDebounce } from 'use-debounce';
import './App.css';

const GameState = {
    Loading: 'Loading',
    Ready: 'Ready',
    Started: 'Started'
}
export default function App() {
    const [name, setName] = useState<string>('');
    const [shapeSide, setShapeSide] = useState<string>('');
    const [id, setId] = useState<NodeJS.Timer>();
    const [gameState, setGameState] = useState<string>();
    // const [isGameStarted, setIsGameStarted] = useState<boolean>();
    // const [shapeSideDebounced] = useDebounce(shapeSide, 1000);

    useEffect(() => {
        console.log('useEffect')
        if (gameState === GameState.Ready && !id) {
            const id = setTimeout(() => {
                setGameState(GameState.Loading)
                setId(undefined)
            }, 3000)
            setId(id)
        } else if (gameState === GameState.Loading && !id) {
            setGameState(GameState.Started)
            const id = setInterval(() => {
                // const Marh
                const screenSide = Math.random() > 0.5 ? 'right' : 'left';
                console.log(screenSide)
                setShapeSide(screenSide)
                setId(undefined)
            }, 1000)
            setId(id)
        }
    }, [gameState, id])
    // randomizeShape(1000)
    // if (!id) {
    //     randomizeShape(1000)
    // }

    // const randomizeShape = (milis: number) => {
    //     console.log('randomizeShape')
    //     // const id = setInterval(() => {
    //     //     // const Marh
    //     //     const screenSide = Math.random() > 0.5 ? 'right' : 'left';
    //     //     setShapeSide(screenSide)
    //     // }, milis)
    //     setId(id)
    // }
    const onChangeName = (event: any) => {
        setName(event.target.value)
    }

    const onRestartClick = () => {
        console.log('onRestartClick')
        setGameState(GameState.Loading)
        // randomizeShape(3000)
    }


    return (
        <div className="App">
            {/* Header */}
            <h1>Shape-Game</h1>

            {/* Details */}
            <div className={''}>
                <input type="text" value={name} onChange={onChangeName} disabled={!!gameState} />
                <input type="button" value="Start!" onClick={onRestartClick} disabled={!!gameState} />
            </div>

            {/* Image Viewer */}
            {gameState===GameState.Started && shapeSide &&
                <div className={`${shapeSide}-image`}>
                    <img src="/gift.png" className="image" alt="" />
                </div>
            }

        </div>
    )
};
