import React, { useEffect, useState } from 'react';
import './App.css';

export default function App() {
    const [name, setName] = useState('');
    const [shipSide, setShipSide] = useState('');
    const [isGameStarted, setIsGameStarted] = useState<boolean>(false);



    useEffect(() => {
        randomizeShape()
    }, [shipSide])

    const randomizeShape = (milis: number) => {
        setTimeout(() => {
            // const Marh
            const screenSide = Math.random() > 0.5 ? 'right' : 'left';
            setShipSide(screenSide)
        }, milis)
    }
    const onChangeName = (event: any) => {
        setName(event.target.value)
    }

    const onClick = () => {
        setIsGameStarted(true)
        setTimeout(() => {
            // const Marh
            const screenSide = Math.random() > 0.5 ? 'right' : 'left';
            setShipSide(screenSide)
        }, 3000)
    }



    return (
        <div className="App">

            {/* Header */}
            <div className={''}>
                <input type="text" value={name} onChange={onChangeName} />
                <input type="button" value="Start!" onClick={onClick} />
            </div>

            {/* Image Viewer */}
            { shipSide &&
                <div className={`${shipSide}-image`}>
                    <img src="/gift.png" className="image" alt="" />
                </div>
            }

        </div>
    )
};
