import React, { useEffect, useReducer, useState } from 'react';
import classNames from "classnames";
// import { useDebounce } from 'use-debounce';
import './App.css';
import { randomIntFromInterval } from './utils';

const GameState = {
    Loading: 'Loading',
    Ready: 'Ready',
    Started: 'Started'
}
const ShapeSide = {
    Left: 'left',
    Right: 'right',
}
const LevelStateType = {
    Mistake: ''
}
export default function App() {
    const [name, setName] = useState<string>('');
    const [gameState, setGameState] = useState<string>();
    const [shapeSide, setShapeSide] = useState<string>();
    const [userAnswer, setUserAnswer] = useState<string>();
    const [levelState, setLevelState] = useState<string>();
    const [levelMessage, setLevelMessage] = useState<string>();
    const [isGameOver, setIsGameOver] = useState<boolean>();
    const [levelInfo, setLevelInfo] = useReducer({})
    const [level, setLevel] = useState<number>(0);
    const [id, setId] = useState<NodeJS.Timer>();

    useEffect(() => {
        if (gameState === GameState.Loading && !id) {
            const LoadingTime = randomIntFromInterval(2000,5000)
            const id = setTimeout(() => {
                setGameState(GameState.Ready)
                setId(undefined)
            }, LoadingTime)
            setId(id)
        } else if (gameState === GameState.Ready) {
            setTimeout(() => {
                // Change Game state
                setGameState(GameState.Started)
                setId(undefined)
                const id = setInterval(() => {
                    // Change image side
                    const screenSide = Math.random() > 0.5 ? ShapeSide.Right : ShapeSide.Left;
                    console.log(screenSide)
                    setUserAnswer('')
                    setShapeSide(screenSide)
                    setLevel(prev => prev+1)
                }, 1000)
                setId(id)
                window.addEventListener('keypress', e => {
                    if (!userAnswer) setUserAnswer(e.key.toLowerCase())
                });
            }, 1000)
        } else if (gameState === GameState.Started) {
            if (userAnswer && shapeSide) {
                if ((userAnswer === 'a' && shapeSide === ShapeSide.Left)
                || (userAnswer === 'l' && shapeSide === ShapeSide.Right)) {
                    setLevelState()
                    // setIsLevelSuccess(true)
                } else {
                    // setIsLevelSuccess(false)
                }
                // if (userAnswer === 'a' && shapeSide === ShapeSide.Left) setIsLevelSuccess(true) = 'hit';
                // else if (userAnswer === 'l' && shapeSide === ShapeSide.Right) setIsLevelSuccess(true) = 'hit'
                // else answerClass = 'fail'
            }
            // if (!isLevelSuccess) {
            //     // Game Over
            // }
        }
    }, [gameState, id, userAnswer])

    const onChangeName = (event: any) => {
        setName(event.target.value)
    }

    const onRestartClick = () => {
        setGameState(GameState.Loading)
    }

    let answerClass = '';

    return (
        <div className="App">
            {/* Header */}
            <h1>Shape-Game</h1>

            {/* Details */}
            <div>
                <input type="text" placeholder='Your name' value={name} onChange={onChangeName} disabled={!!gameState} />
                <input type="button" value="Start Game!" onClick={onRestartClick} disabled={!!gameState} />
            </div>

            {/* Image Viewer */}
            {gameState === GameState.Loading &&
                <div>Loading...</div>
            }
            {gameState === GameState.Ready &&
                <div>Ready!</div>
            }
            {gameState === GameState.Started && shapeSide &&
                <>
                    <div>Level:<span>{level}</span></div>
                    <div className={`${shapeSide}-image ${answerClass}`}>
                        <img src="/gift.png" className="image" alt="" />
                    </div>
                </>
            }

        </div>
    )
};
